sys_transform_script.script:
(function runTransformScript(source, target, log, map) {

    var projectName = source.u_projeto ? ("" + source.u_projeto).trim().toLowerCase() : "";
    var taskName = source.u_task ? ("" + source.u_task).trim().toLowerCase() : "";

    log.info("Verificando Projeto: " + projectName + " | Tarefa: " + taskName);

    // Verificação do projeto
    var projectGr = new GlideRecord('x_1593770_mock_ski_projetos');
    projectGr.addQuery('nome_projeto', projectName);
    projectGr.query();

    if (!projectGr.next()) {
        log.error("Erro na linha " + source.sys_import_row + ": Projeto não encontrado.");
        return false;
    }

    var projectId = projectGr.getUniqueValue();
    log.info("Projeto encontrado! ID: " + projectId);

    // Verificação da tarefa
    var taskGr = new GlideRecord('x_1593770_mock_ski_tarefa');
    taskGr.addQuery('nome_tarefa', taskName);
    taskGr.addQuery('parent', projectId);
    taskGr.query();

    if (!taskGr.next()) {
        log.error("Erro na linha " + source.sys_import_row + ": Tarefa não encontrada.");
        return false;
    }

    var taskId = taskGr.getUniqueValue();
    log.info("Tarefa encontrada! ID: " + taskId);

    // Definição dos valores no target
    target.project = projectId;
    target.task = taskId;

    return true;

})(source, target, log, map);
